import { 
  Container, 
  Typography, 
  Box, 
  Button,
  Grid
} from '@mui/material';
import { KeyboardArrowRight } from '@mui/icons-material';

function Home() {
  return (
    <Box 
      sx={{ 
        background: 'linear-gradient(to bottom, #171717 0%, #262626 100%)',
        minHeight: '100vh',
        color: 'white',
        pt: 12,
        pb: 8
      }}
    >
      <Container maxWidth="lg">
        <Grid container spacing={4} alignItems="center">
          <Grid item xs={12} md={6}>
            <Box sx={{ mb: 4 }}>
              <Typography 
                variant="h1"
                sx={{ 
                  fontWeight: 800,
                  fontSize: { xs: '2.5rem', md: '3.5rem' },
                  mb: 2,
                  background: 'linear-gradient(45deg, #dc2626 30%, #ef4444 90%)',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  lineHeight: 1.2
                }}
              >
                Experience Pure
                <br />
                Performance
              </Typography>
              <Typography 
                variant="h5" 
                sx={{ 
                  color: 'rgba(255,255,255,0.8)',
                  mb: 4,
                  fontWeight: 400
                }}
              >
                Discover our exclusive collection of premium vehicles designed for those who demand excellence.
              </Typography>
              <Box sx={{ display: 'flex', gap: 2 }}>
                <Button 
                  variant="contained"
                  size="large"
                  endIcon={<KeyboardArrowRight />}
                  sx={{ 
                    backgroundColor: '#dc2626',
                    '&:hover': {
                      backgroundColor: '#b91c1c',
                      transform: 'translateY(-2px)',
                      boxShadow: '0 4px 12px rgba(220, 38, 38, 0.4)'
                    },
                    transition: 'all 0.2s ease-in-out'
                  }}
                >
                  View Collection
                </Button>
                <Button 
                  variant="outlined"
                  size="large"
                  sx={{ 
                    color: 'white',
                    borderColor: 'rgba(255,255,255,0.5)',
                    '&:hover': {
                      borderColor: 'white',
                      backgroundColor: 'rgba(255,255,255,0.1)'
                    }
                  }}
                >
                  Learn More
                </Button>
              </Box>
            </Box>
          </Grid>
          <Grid item xs={12} md={6}>
            <Box 
              sx={{ 
                position: 'relative',
                '&::before': {
                  content: '""',
                  position: 'absolute',
                  top: 0,
                  left: '50%',
                  transform: 'translateX(-50%)',
                  width: '200px',
                  height: '200px',
                  background: '#dc2626',
                  filter: 'blur(100px)',
                  opacity: 0.3,
                  zIndex: 0
                }
              }}
            >
              <img 
                src="https://source.unsplash.com/random/800x600/?luxury-car"
                alt="Luxury Car"
                style={{ 
                  width: '100%',
                  height: 'auto',
                  borderRadius: '12px',
                  position: 'relative',
                  zIndex: 1
                }}
              />
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
}

export default Home;
