import { AppBar, Toolbar, Button, Box } from '@mui/material';
import { HomeRounded, PersonRounded, LoginRounded } from '@mui/icons-material';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';

function Navigation() {
  return (
    <AppBar 
      position="fixed" 
      sx={{ 
        zIndex: (theme) => theme.zIndex.drawer + 1,
        backgroundColor: 'rgba(25, 118, 210, 0.95)', // Semi-transparent background
        backdropFilter: 'blur(8px)', // Blur effect for better readability
      }}
    >
      <Toolbar>
        <Box sx={{ flexGrow: 1, display: 'flex', gap: 2 }}>
          <motion.div
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            style={{ zIndex: 2000 }} // Ensure clickable
          >
            <Button
              component={Link}
              to="/"
              color="inherit"
              startIcon={<HomeRounded />}
              sx={{ 
                '&:hover': { 
                  backgroundColor: 'rgba(255, 255, 255, 0.1)'
                }
              }}
            >
              Home
            </Button>
          </motion.div>
          
          <motion.div
            whileHover={{ scale: 1.1 }}
            whileTap={{ scale: 0.95 }}
            style={{ zIndex: 2000 }} // Ensure clickable
          >
            <Button
              component={Link}
              to="/profile"
              color="inherit"
              startIcon={<PersonRounded />}
              sx={{ 
                '&:hover': { 
                  backgroundColor: 'rgba(255, 255, 255, 0.1)'
                }
              }}
            >
              Profile
            </Button>
          </motion.div>
        </Box>
        
        <motion.div
          whileHover={{ scale: 1.1 }}
          whileTap={{ scale: 0.95 }}
          style={{ zIndex: 2000 }} // Ensure clickable
        >
          <Button
            component={Link}
            to="/login"
            color="inherit"
            startIcon={<LoginRounded />}
            sx={{ 
              '&:hover': { 
                backgroundColor: 'rgba(255, 255, 255, 0.1)'
              }
            }}
          >
            Login
          </Button>
        </motion.div>
      </Toolbar>
    </AppBar>
  );
}

export default Navigation;
