import { 
  Container, 
  Typography, 
  Box, 
  Button,
  Grid,
  Card,
  CardContent,
  Menu,
  MenuItem,
  IconButton,
  TextField,
  ImageList,
  ImageListItem,
  Link,
  CardMedia,
  CircularProgress
} from '@mui/material';
import { 
  KeyboardArrowRight, 
  Speed, 
  LocalGasStation, 
  Timer, 
  Star,
  KeyboardArrowDown,
  Facebook,
  Instagram,
  Twitter,
  YouTube,
  Phone,
  Email,
  LocationOn
} from '@mui/icons-material';
import { useState, useEffect } from 'react';

function Home() {
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedMake, setSelectedMake] = useState(null);
  const [selectedModel, setSelectedModel] = useState(null);
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const features = [
    { icon: <Speed />, title: "Affordable", value: "Great Prices" },
    { icon: <LocalGasStation />, title: "Efficient", value: "Low Running Costs" },
    { icon: <Timer />, title: "Finance", value: "Easy Terms" },
    { icon: <Star />, title: "Rating", value: "4.8/5.0" }
  ];

  const carMakes = {
    "BMW": ["1 Series", "2 Series", "3 Series", "4 Series", "5 Series"],
    "Audi": ["A1", "A3", "A4", "A5", "A6"],
    "Mercedes": ["A-Class", "B-Class", "C-Class", "E-Class", "S-Class"],
    "Ford": ["Fiesta", "Focus", "Puma", "Kuga", "Mondeo"],
    "Volkswagen": ["Polo", "Golf", "Tiguan", "Passat", "T-Roc"]
  };

  const bmw1Series = [
    {
      id: 1,
      image: "/images/bmw1.jpg",
      title: "BMW 1 Series 118i M Sport",
      price: "£21,995",
      year: "2020",
      mileage: "25,000 miles",
      fuel: "Petrol"
    },
    {
      id: 2,
      image: "/images/bmw2.jpg",
      title: "BMW 1 Series 120d Sport",
      price: "£19,995",
      year: "2019",
      mileage: "35,000 miles",
      fuel: "Diesel"
    }
  ];

  useEffect(() => {
    const fetchInstagramPosts = async () => {
      try {
        const response = await fetch(
          `https://graph.instagram.com/me/media?fields=id,caption,media_type,media_url,permalink,thumbnail_url,timestamp&access_token=${process.env.REACT_APP_INSTAGRAM_ACCESS_TOKEN}&limit=3`
        );
        
        if (!response.ok) {
          throw new Error('Failed to fetch Instagram posts');
        }

        const data = await response.json();
        setPosts(data.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching Instagram posts:', err);
        setError('Failed to load Instagram posts');
        setLoading(false);
      }
    };

    fetchInstagramPosts();
  }, []);

  const handleMakeClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMakeSelect = (make) => {
    setSelectedMake(make);
    setSelectedModel(null);
    setAnchorEl(null);
  };

  const handleModelSelect = (model) => {
    setSelectedModel(model);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
  };

  return (
    <Box sx={{ backgroundColor: '#171717', color: 'white', minHeight: '100vh' }}>
      {/* Hero Section */}
      <Box sx={{ pt: 12, pb: 8, background: 'linear-gradient(to bottom, #171717 0%, #262626 100%)' }}>
        <Container maxWidth="lg">
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={6}>
              <Box sx={{ mb: 4 }}>
                <Typography 
                  variant="h1"
                  sx={{ 
                    fontWeight: 800,
                    fontSize: { xs: '2.5rem', md: '3.5rem' },
                    mb: 2,
                    background: 'linear-gradient(45deg, #dc2626 30%, #ef4444 90%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    lineHeight: 1.2
                  }}
                >
                  Find Your
                  <br />
                  Perfect Car
                </Typography>
                <Typography 
                  variant="h5" 
                  sx={{ 
                    color: 'rgba(255,255,255,0.8)',
                    mb: 4,
                    fontWeight: 400
                  }}
                >
                  Browse our selection of quality used cars at competitive prices.
                </Typography>
              </Box>
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* Car Search Section - Now Second */}
      <Box sx={{ py: 8, backgroundColor: '#1a1a1a' }}>
        <Container maxWidth="lg">
          <Typography 
            variant="h3" 
            sx={{ 
              textAlign: 'center', 
              mb: 4,
              fontWeight: 700
            }}
          >
            Find Your Perfect Car
          </Typography>
          
          <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mb: 4 }}>
            <Button
              variant="outlined"
              onClick={handleMakeClick}
              endIcon={<KeyboardArrowDown />}
              sx={{ 
                minWidth: '200px',
                color: 'white',
                borderColor: 'rgba(255,255,255,0.3)'
              }}
            >
              {selectedMake || "Select Make"}
            </Button>
            
            {selectedMake && (
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                {carMakes[selectedMake].map((model) => (
                  <Button
                    key={model}
                    variant={selectedModel === model ? "contained" : "outlined"}
                    onClick={() => handleModelSelect(model)}
                    sx={{ 
                      color: 'white',
                      borderColor: 'rgba(255,255,255,0.3)'
                    }}
                  >
                    {model}
                  </Button>
                ))}
              </Box>
            )}
          </Box>

          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={() => setAnchorEl(null)}
            PaperProps={{
              sx: {
                backgroundColor: '#262626',
                color: 'white',
                mt: 1
              }
            }}
          >
            {Object.keys(carMakes).map((make) => (
              <MenuItem 
                key={make} 
                onClick={() => handleMakeSelect(make)}
                sx={{
                  '&:hover': {
                    backgroundColor: 'rgba(255,255,255,0.1)'
                  }
                }}
              >
                {make}
              </MenuItem>
            ))}
          </Menu>

          {/* Display BMW 1 Series Cars */}
          {selectedMake === "BMW" && selectedModel === "1 Series" && (
            <Grid container spacing={3}>
              {bmw1Series.map((car) => (
                <Grid item xs={12} md={4} key={car.id}>
                  <Card 
                    sx={{ 
                      backgroundColor: '#262626',
                      transition: 'transform 0.2s',
                      '&:hover': {
                        transform: 'translateY(-8px)'
                      }
                    }}
                  >
                    <Box 
                      component="img"
                      src={car.image}
                      alt={car.title}
                      sx={{
                        width: '100%',
                        height: '200px',
                        objectFit: 'cover'
                      }}
                    />
                    <CardContent>
                      <Typography variant="h6" sx={{ mb: 1 }}>
                        {car.title}
                      </Typography>
                      <Typography variant="h5" sx={{ color: '#dc2626', mb: 2 }}>
                        {car.price}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                        {car.year} • {car.mileage} • {car.fuel}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
        </Container>
      </Box>

      {/* Instagram Feed Section */}
      <Box sx={{ py: 8, backgroundColor: '#171717' }}>
        <Container maxWidth="lg">
          <Box sx={{ textAlign: 'center', mb: 6 }}>
            <Typography variant="h3" sx={{ mb: 2, fontWeight: 700 }}>
              Latest From Instagram
            </Typography>
          </Box>
          
          {loading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center' }}>
              <CircularProgress sx={{ color: '#dc2626' }} />
            </Box>
          ) : error ? (
            <Typography color="error" align="center">{error}</Typography>
          ) : (
            <Grid container spacing={3}>
              {posts.map((post) => (
                <Grid item xs={12} md={4} key={post.id}>
                  <Card 
                    sx={{ 
                      backgroundColor: '#262626',
                      height: '100%',
                      transition: 'transform 0.2s',
                      '&:hover': {
                        transform: 'translateY(-8px)'
                      }
                    }}
                  >
                    <CardMedia
                      component="img"
                      image={post.media_url}
                      alt={post.caption?.slice(0, 100) || 'Instagram post'}
                      sx={{
                        height: 300,
                        objectFit: 'cover'
                      }}
                    />
                    <CardContent>
                      <Typography 
                        variant="body2" 
                        sx={{ 
                          color: 'rgba(255,255,255,0.7)',
                          mb: 2,
                          display: '-webkit-box',
                          WebkitLineClamp: 3,
                          WebkitBoxOrient: 'vertical',
                          overflow: 'hidden'
                        }}
                      >
                        {post.caption}
                      </Typography>
                      <Button
                        variant="outlined"
                        size="small"
                        href={post.permalink}
                        target="_blank"
                        startIcon={<Instagram />}
                        sx={{ 
                          borderColor: '#dc2626',
                          color: 'white',
                          '&:hover': {
                            borderColor: '#b91c1c',
                            backgroundColor: 'rgba(220, 38, 38, 0.1)'
                          }
                        }}
                      >
                        View Post
                      </Button>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
        </Container>
      </Box>

      {/* About Us Section - Now Last */}
      <Box sx={{ py: 8, backgroundColor: '#171717' }}>
        <Container maxWidth="lg">
          <Grid container spacing={6} alignItems="center">
            <Grid item xs={12} md={6}>
              <Box
                component="img"
                src="/images/showroom.jpg"
                alt="Our Showroom"
                sx={{
                  width: '100%',
                  height: 'auto',
                  borderRadius: 2,
                  boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)',
                }}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="h3" sx={{ mb: 3, fontWeight: 700 }}>
                About Us
              </Typography>
              <Typography variant="body1" sx={{ mb: 3, color: 'rgba(255,255,255,0.8)' }}>
                Welcome to [Showroom Name], your trusted local car dealership serving the community since 2010. We specialize in quality used cars and affordable new vehicles, making car ownership accessible to everyone.
              </Typography>
              <Typography variant="body1" sx={{ mb: 4, color: 'rgba(255,255,255,0.8)' }}>
                Our team of experienced professionals is dedicated to helping you find the perfect car that fits both your needs and budget. We offer competitive finance options and outstanding after-sales service.
              </Typography>
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* Contact Us Section */}
      <Box sx={{ py: 8, backgroundColor: '#1a1a1a' }}>
        <Container maxWidth="lg">
          <Grid container spacing={6}>
            <Grid item xs={12} md={6}>
              <Typography variant="h3" sx={{ mb: 4, fontWeight: 700 }}>
                Contact Us
              </Typography>
              
              <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                <TextField
                  label="Name"
                  variant="outlined"
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(255,255,255,0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: 'rgba(255,255,255,0.5)',
                      },
                    },
                    '& .MuiInputLabel-root': {
                      color: 'rgba(255,255,255,0.7)',
                    },
                    '& .MuiInputBase-input': {
                      color: 'white',
                    },
                  }}
                />
                
                <TextField
                  label="Email"
                  type="email"
                  variant="outlined"
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(255,255,255,0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: 'rgba(255,255,255,0.5)',
                      },
                    },
                    '& .MuiInputLabel-root': {
                      color: 'rgba(255,255,255,0.7)',
                    },
                    '& .MuiInputBase-input': {
                      color: 'white',
                    },
                  }}
                />
                
                <TextField
                  label="Message"
                  multiline
                  rows={4}
                  variant="outlined"
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(255,255,255,0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: 'rgba(255,255,255,0.5)',
                      },
                    },
                    '& .MuiInputLabel-root': {
                      color: 'rgba(255,255,255,0.7)',
                    },
                    '& .MuiInputBase-input': {
                      color: 'white',
                    },
                  }}
                />
                
                <Button 
                  type="submit"
                  variant="contained"
                  size="large"
                  sx={{
                    backgroundColor: '#dc2626',
                    '&:hover': {
                      backgroundColor: '#b91c1c',
                    },
                  }}
                >
                  Send Message
                </Button>
              </Box>
            </Grid>
            
            <Grid item xs={12} md={6}>
              <Box sx={{ pl: { md: 4 } }}>
                <Typography variant="h5" sx={{ mb: 4, fontWeight: 600 }}>
                  Get in Touch
                </Typography>
                
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Phone sx={{ color: '#dc2626' }} />
                    <Box>
                      <Typography variant="body1" sx={{ fontWeight: 600 }}>
                        Phone
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                        +44 (0) 123 456 7890
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <Email sx={{ color: '#dc2626' }} />
                    <Box>
                      <Typography variant="body1" sx={{ fontWeight: 600 }}>
                        Email
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                        info@carshowroom.com
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <LocationOn sx={{ color: '#dc2626' }} />
                    <Box>
                      <Typography variant="body1" sx={{ fontWeight: 600 }}>
                        Address
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                        123 Car Street, Automobile City, AB12 3CD
                      </Typography>
                    </Box>
                  </Box>
                </Box>
              </Box>
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* Footer with Social Media */}
      <Box sx={{ py: 4, backgroundColor: '#0a0a0a' }}>
        <Container maxWidth="lg">
          <Box sx={{ 
            display: 'flex', 
            justifyContent: 'center', 
            gap: 2,
            mb: 3
          }}>
            <IconButton 
              href="https://facebook.com" 
              target="_blank"
              sx={{ 
                color: 'white',
                '&:hover': { color: '#dc2626' }
              }}
            >
              <Facebook />
            </IconButton>
            <IconButton 
              href="https://instagram.com" 
              target="_blank"
              sx={{ 
                color: 'white',
                '&:hover': { color: '#dc2626' }
              }}
            >
              <Instagram />
            </IconButton>
            <IconButton 
              href="https://twitter.com" 
              target="_blank"
              sx={{ 
