import { 
  Container, 
  Typography, 
  Box, 
  Button,
  Grid,
  Card,
  CardContent,
  Menu,
  MenuItem,
  IconButton
} from '@mui/material';
import { 
  KeyboardArrowRight, 
  Speed, 
  LocalGasStation, 
  Timer, 
  Star,
  KeyboardArrowDown
} from '@mui/icons-material';
import { useState } from 'react';

function Home() {
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedMake, setSelectedMake] = useState(null);
  const [selectedModel, setSelectedModel] = useState(null);

  const features = [
    { icon: <Speed />, title: "Affordable", value: "Great Prices" },
    { icon: <LocalGasStation />, title: "Efficient", value: "Low Running Costs" },
    { icon: <Timer />, title: "Finance", value: "Easy Terms" },
    { icon: <Star />, title: "Rating", value: "4.8/5.0" }
  ];

  const carMakes = {
    "BMW": ["1 Series", "2 Series", "3 Series", "4 Series", "5 Series"],
    "Audi": ["A1", "A3", "A4", "A5", "A6"],
    "Mercedes": ["A-Class", "B-Class", "C-Class", "E-Class", "S-Class"],
    "Ford": ["Fiesta", "Focus", "Puma", "Kuga", "Mondeo"],
    "Volkswagen": ["Polo", "Golf", "Tiguan", "Passat", "T-Roc"]
  };

  const bmw1Series = [
    {
      id: 1,
      image: "/images/bmw1.jpg",
      title: "BMW 1 Series 118i M Sport",
      price: "£21,995",
      year: "2020",
      mileage: "25,000 miles",
      fuel: "Petrol"
    },
    {
      id: 2,
      image: "/images/bmw2.jpg",
      title: "BMW 1 Series 120d Sport",
      price: "£19,995",
      year: "2019",
      mileage: "35,000 miles",
      fuel: "Diesel"
    }
  ];

  const handleMakeClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMakeSelect = (make) => {
    setSelectedMake(make);
    setSelectedModel(null);
    setAnchorEl(null);
  };

  const handleModelSelect = (model) => {
    setSelectedModel(model);
  };

  return (
    <Box sx={{ backgroundColor: '#171717', color: 'white', minHeight: '100vh' }}>
      {/* Hero Section */}
      <Box sx={{ pt: 12, pb: 8, background: 'linear-gradient(to bottom, #171717 0%, #262626 100%)' }}>
        <Container maxWidth="lg">
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={6}>
              <Box sx={{ mb: 4 }}>
                <Typography 
                  variant="h1"
                  sx={{ 
                    fontWeight: 800,
                    fontSize: { xs: '2.5rem', md: '3.5rem' },
                    mb: 2,
                    background: 'linear-gradient(45deg, #dc2626 30%, #ef4444 90%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    lineHeight: 1.2
                  }}
                >
                  Find Your
                  <br />
                  Perfect Car
                </Typography>
                <Typography 
                  variant="h5" 
                  sx={{ 
                    color: 'rgba(255,255,255,0.8)',
                    mb: 4,
                    fontWeight: 400
                  }}
                >
                  Browse our selection of quality used cars at competitive prices.
                </Typography>
              </Box>
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* About Us Section - New */}
      <Box sx={{ py: 8, backgroundColor: '#1a1a1a' }}>
        <Container maxWidth="lg">
          <Grid container spacing={6} alignItems="center">
            <Grid item xs={12} md={6}>
              <Box
                component="img"
                src="https://images.unsplash.com/photo-1486006920555-c77dcf18193c?w=1200&auto=format&fit=crop"
                alt="Our Showroom"
                sx={{
                  width: '100%',
                  height: 'auto',
                  borderRadius: 2,
                  boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)',
                  objectFit: 'cover'
                }}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="h3" sx={{ mb: 3, fontWeight: 700 }}>
                About Us
              </Typography>
              <Typography variant="body1" sx={{ mb: 3, color: 'rgba(255,255,255,0.8)' }}>
                Welcome to [Showroom Name], your trusted local car dealership serving the community since 2010. We specialize in quality used cars and affordable new vehicles, making car ownership accessible to everyone.
              </Typography>
              <Typography variant="body1" sx={{ mb: 4, color: 'rgba(255,255,255,0.8)' }}>
                Our team of experienced professionals is dedicated to helping you find the perfect car that fits both your needs and budget. We offer competitive finance options and outstanding after-sales service.
              </Typography>
              <Box sx={{ display: 'flex', gap: 3 }}>
                <Box>
                  <Typography variant="h4" sx={{ color: '#dc2626', fontWeight: 700 }}>
                    1000+
                  </Typography>
                  <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                    Happy Customers
                  </Typography>
                </Box>
                <Box>
                  <Typography variant="h4" sx={{ color: '#dc2626', fontWeight: 700 }}>
                    500+
                  </Typography>
                  <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                    Cars Sold
                  </Typography>
                </Box>
                <Box>
                  <Typography variant="h4" sx={{ color: '#dc2626', fontWeight: 700 }}>
                    4.8
                  </Typography>
                  <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                    Average Rating
                  </Typography>
                </Box>
              </Box>
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* Features Section */}
      <Box sx={{ py: 8, backgroundColor: '#1a1a1a' }}>
        <Container maxWidth="lg">
          <Grid container spacing={4}>
            {features.map((feature, index) => (
              <Grid item xs={12} sm={6} md={3} key={index}>
                <Card 
                  sx={{ 
                    backgroundColor: '#262626',
                    color: 'white',
                    height: '100%',
                    transition: 'transform 0.2s',
                    '&:hover': {
                      transform: 'translateY(-8px)',
                      backgroundColor: '#2d2d2d'
                    }
                  }}
                >
                  <CardContent sx={{ textAlign: 'center' }}>
                    <Box sx={{ color: '#dc2626', mb: 2 }}>
                      {feature.icon}
                    </Box>
                    <Typography variant="h6" sx={{ mb: 1 }}>
                      {feature.title}
                    </Typography>
                    <Typography variant="h4" sx={{ color: '#dc2626', fontWeight: 'bold' }}>
                      {feature.value}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Car Search Section */}
      <Box sx={{ py: 8, backgroundColor: '#171717' }}>
        <Container maxWidth="lg">
          <Typography 
            variant="h3" 
            sx={{ 
              textAlign: 'center', 
              mb: 4,
              fontWeight: 700
            }}
          >
            Find Your Perfect Car
          </Typography>
          
          <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mb: 4 }}>
            <Button
              variant="outlined"
              onClick={handleMakeClick}
              endIcon={<KeyboardArrowDown />}
              sx={{ 
                minWidth: '200px',
                color: 'white',
                borderColor: 'rgba(255,255,255,0.3)'
              }}
            >
              {selectedMake || "Select Make"}
            </Button>
            
            {selectedMake && (
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                {carMakes[selectedMake].map((model) => (
                  <Button
                    key={model}
                    variant={selectedModel === model ? "contained" : "outlined"}
                    onClick={() => handleModelSelect(model)}
                    sx={{ 
                      color: 'white',
                      borderColor: 'rgba(255,255,255,0.3)'
                    }}
                  >
                    {model}
                  </Button>
                ))}
              </Box>
            )}
          </Box>

          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={() => setAnchorEl(null)}
            PaperProps={{
              sx: {
                backgroundColor: '#262626',
                color: 'white',
                mt: 1
              }
            }}
          >
            {Object.keys(carMakes).map((make) => (
              <MenuItem 
                key={make} 
                onClick={() => handleMakeSelect(make)}
                sx={{
                  '&:hover': {
                    backgroundColor: 'rgba(255,255,255,0.1)'
                  }
                }}
              >
                {make}
              </MenuItem>
            ))}
          </Menu>

          {/* Display BMW 1 Series Cars */}
          {selectedMake === "BMW" && selectedModel === "1 Series" && (
            <Grid container spacing={3}>
              {bmw1Series.map((car) => (
                <Grid item xs={12} md={4} key={car.id}>
                  <Card 
                    sx={{ 
                      backgroundColor: '#262626',
                      transition: 'transform 0.2s',
                      '&:hover': {
                        transform: 'translateY(-8px)'
                      }
                    }}
                  >
                    <Box 
                      component="img"
                      src={car.image}
                      alt={car.title}
                      sx={{
                        width: '100%',
                        height: '200px',
                        objectFit: 'cover'
                      }}
                    />
                    <CardContent>
                      <Typography variant="h6" sx={{ mb: 1 }}>
                        {car.title}
                      </Typography>
                      <Typography variant="h5" sx={{ color: '#dc2626', mb: 2 }}>
                        {car.price}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                        {car.year} • {car.mileage} • {car.fuel}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
        </Container>
      </Box>
    </Box>
  );
}

export default Home;
