import { 
  Container, 
  Typography, 
  Box, 
  Button,
  Grid,
  Card,
  CardContent,
  Menu,
  MenuItem,
  IconButton,
  TextField,
  ImageList,
  ImageListItem,
  Link
} from '@mui/material';
import { 
  KeyboardArrowRight, 
  Speed, 
  LocalGasStation, 
  Timer, 
  Star,
  KeyboardArrowDown,
  Facebook,
  Instagram,
  Twitter,
  YouTube,
  Phone,
  Email,
  LocationOn
} from '@mui/icons-material';
import { useState } from 'react';

function Home() {
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedMake, setSelectedMake] = useState(null);
  const [selectedModel, setSelectedModel] = useState(null);

  const features = [
    { icon: <Speed />, title: "Affordable", value: "Great Prices" },
    { icon: <LocalGasStation />, title: "Efficient", value: "Low Running Costs" },
    { icon: <Timer />, title: "Finance", value: "Easy Terms" },
    { icon: <Star />, title: "Rating", value: "4.8/5.0" }
  ];

  const carMakes = {
    "BMW": ["1 Series", "2 Series", "3 Series", "4 Series", "5 Series"],
    "Audi": ["A1", "A3", "A4", "A5", "A6"],
    "Mercedes": ["A-Class", "B-Class", "C-Class", "E-Class", "S-Class"],
    "Ford": ["Fiesta", "Focus", "Puma", "Kuga", "Mondeo"],
    "Volkswagen": ["Polo", "Golf", "Tiguan", "Passat", "T-Roc"]
  };

  const bmw1Series = [
    {
      id: 1,
      image: "/images/bmw1.jpg",
      title: "BMW 1 Series 118i M Sport",
      price: "Â£21,995",
      year: "2020",
      mileage: "25,000 miles",
      fuel: "Petrol"
    },
    {
      id: 2,
      image: "/images/bmw2.jpg",
      title: "BMW 1 Series 120d Sport",
      price: "Â£19,995",
      year: "2019",
      mileage: "35,000 miles",
      fuel: "Diesel"
    }
  ];

  // Placeholder Instagram posts - replace with real Instagram API data later
  const instagramPosts = [
    {
      id: 1,
      image: "/images/insta1.jpg",
      link: "https://instagram.com/p/1",
      caption: "Just arrived: BMW 1 Series in Alpine White! ðŸš—âœ¨"
    },
    {
      id: 2,
      image: "/images/insta2.jpg",
      link: "https://instagram.com/p/2",
      caption: "Happy customer driving away in their new car! ðŸŽ‰"
    },
    {
      id: 3,
      image: "/images/insta3.jpg",
      link: "https://instagram.com/p/3",
      caption: "Weekend vibes at the showroom ðŸŒŸ"
    },
    {
      id: 4,
      image: "/images/insta4.jpg",
      link: "https://instagram.com/p/4",
      caption: "New stock just landed! ðŸš—"
    },
    {
      id: 5,
      image: "/images/insta5.jpg",
      link: "https://instagram.com/p/5",
      caption: "Behind the scenes at our service center ðŸ”§"
    },
    {
      id: 6,
      image: "/images/insta6.jpg",
      link: "https://instagram.com/p/6",
      caption: "Special offer weekend! ðŸ’«"
    }
  ];

  const handleMakeClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMakeSelect = (make) => {
    setSelectedMake(make);
    setSelectedModel(null);
    setAnchorEl(null);
  };

  const handleModelSelect = (model) => {
    setSelectedModel(model);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
  };

  return (
    <Box sx={{ backgroundColor: '#171717', color: 'white', minHeight: '100vh' }}>
      {/* Hero Section */}
      <Box sx={{ pt: 12, pb: 8, background: 'linear-gradient(to bottom, #171717 0%, #262626 100%)' }}>
        <Container maxWidth="lg">
          <Grid container spacing={4} alignItems="center">
            <Grid item xs={12} md={6}>
              <Box sx={{ mb: 4 }}>
                <Typography 
                  variant="h1"
                  sx={{ 
                    fontWeight: 800,
                    fontSize: { xs: '2.5rem', md: '3.5rem' },
                    mb: 2,
                    background: 'linear-gradient(45deg, #dc2626 30%, #ef4444 90%)',
                    WebkitBackgroundClip: 'text',
                    WebkitTextFillColor: 'transparent',
                    lineHeight: 1.2
                  }}
                >
                  Find Your
                  <br />
                  Perfect Car
                </Typography>
                <Typography 
                  variant="h5" 
                  sx={{ 
                    color: 'rgba(255,255,255,0.8)',
                    mb: 4,
                    fontWeight: 400
                  }}
                >
                  Browse our selection of quality used cars at competitive prices.
                </Typography>
              </Box>
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* Car Search Section - Now Second */}
      <Box sx={{ py: 8, backgroundColor: '#1a1a1a' }}>
        <Container maxWidth="lg">
          <Typography 
            variant="h3" 
            sx={{ 
              textAlign: 'center', 
              mb: 4,
              fontWeight: 700
            }}
          >
            Find Your Perfect Car
          </Typography>
          
          <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mb: 4 }}>
            <Button
              variant="outlined"
              onClick={handleMakeClick}
              endIcon={<KeyboardArrowDown />}
              sx={{ 
                minWidth: '200px',
                color: 'white',
                borderColor: 'rgba(255,255,255,0.3)'
              }}
            >
              {selectedMake || "Select Make"}
            </Button>
            
            {selectedMake && (
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                {carMakes[selectedMake].map((model) => (
                  <Button
                    key={model}
                    variant={selectedModel === model ? "contained" : "outlined"}
                    onClick={() => handleModelSelect(model)}
                    sx={{ 
                      color: 'white',
                      borderColor: 'rgba(255,255,255,0.3)'
                    }}
                  >
                    {model}
                  </Button>
                ))}
              </Box>
            )}
          </Box>

          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={() => setAnchorEl(null)}
            PaperProps={{
              sx: {
                backgroundColor: '#262626',
                color: 'white',
                mt: 1
              }
            }}
          >
            {Object.keys(carMakes).map((make) => (
              <MenuItem 
                key={make} 
                onClick={() => handleMakeSelect(make)}
                sx={{
                  '&:hover': {
                    backgroundColor: 'rgba(255,255,255,0.1)'
                  }
                }}
              >
                {make}
              </MenuItem>
            ))}
          </Menu>

          {/* Display BMW 1 Series Cars */}
          {selectedMake === "BMW" && selectedModel === "1 Series" && (
            <Grid container spacing={3}>
              {bmw1Series.map((car) => (
                <Grid item xs={12} md={4} key={car.id}>
                  <Card 
                    sx={{ 
                      backgroundColor: '#262626',
                      transition: 'transform 0.2s',
                      '&:hover': {
                        transform: 'translateY(-8px)'
                      }
                    }}
                  >
                    <Box 
                      component="img"
                      src={car.image}
                      alt={car.title}
                      sx={{
                        width: '100%',
                        height: '200px',
                        objectFit: 'cover'
                      }}
                    />
                    <CardContent>
                      <Typography variant="h6" sx={{ mb: 1 }}>
                        {car.title}
                      </Typography>
                      <Typography variant="h5" sx={{ color: '#dc2626', mb: 2 }}>
                        {car.price}
                      </Typography>
                      <Typography variant="body2" sx={{ color: 'rgba(255,255,255,0.7)' }}>
                        {car.year} â€¢ {car.mileage} â€¢ {car.fuel}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          )}
        </Container>
      </Box>

      {/* Instagram Feed Section */}
      <Box sx={{ py: 8, backgroundColor: '#171717' }}>
        <Container maxWidth="lg">
          <Box sx={{ textAlign: 'center', mb: 6 }}>
            <Typography variant="h3" sx={{ mb: 2, fontWeight: 700 }}>
              Follow Us on Instagram
            </Typography>
            <Typography variant="h6" sx={{ color: 'rgba(255,255,255,0.7)', mb: 3 }}>
              @carshowroom
            </Typography>
            <Button
              variant="outlined"
              startIcon={<Instagram />}
              href="https://instagram.com/carshowroom"
              target="_blank"
              sx={{ 
                borderColor: '#dc2626',
                color: 'white',
                '&:hover': {
                  borderColor: '#b91c1c',
                  backgroundColor: 'rgba(220, 38, 38, 0.1)'
                }
              }}
            >
              Follow Us
            </Button>
          </Box>

          <ImageList 
            variant="masonry" 
            cols={{ xs: 1, sm: 2, md: 3 }} 
            gap={24}
            sx={{
              mb: 4,
              '& .MuiImageListItem-root': {
                overflow: 'hidden',
                borderRadius: 2,
              }
            }}
          >
            {instagramPosts.map((post) => (
              <ImageListItem 
                key={post.id}
                sx={{
                  transition: 'transform 0.3s ease-in-out',
                  '&:hover': {
                    transform: 'scale(1.02)',
                    '& .overlay': {
                      opacity: 1
                    }
                  }
                }}
              >
                <Box sx={{ position: 'relative' }}>
                  <Box
                    component="img"
                    src={post.image}
                    alt={post.caption}
                    loading="lazy"
                    sx={{
                      width: '100%',
                      height: 'auto',
                      display: 'block'
                    }}
                  />
                  <Link 
                    href={post.link}
                    target="_blank"
                    underline="none"
                  >
                    <Box
                      className="overlay"
                      sx={{
                        position: 'absolute',
                        top: 0,
                        left: 0,
                        right: 0,
                        bottom: 0,
                        backgroundColor: 'rgba(0,0,0,0.7)',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        opacity: 0,
                        transition: 'opacity 0.3s ease-in-out',
                        p: 2
                      }}
                    >
                      <Typography 
                        variant="body2" 
                        color="white"
                        align="center"
                      >
                        {post.caption}
                      </Typography>
                    </Box>
                  </Link>
                </Box>
              </ImageListItem>
            ))}
          </ImageList>

          <Box sx={{ textAlign: 'center' }}>
            <Button
              variant="contained"
              endIcon={<KeyboardArrowDown />}
              sx={{
                backgroundColor: '#dc2626',
                '&:hover': {
                  backgroundColor: '#b91c1c'
                }
              }}
            >
              Load More
            </Button>
          </Box>
        </Container>
      </Box>

      {/* About Us Section - Now Last */}
      <Box sx={{ py: 8, backgroundColor: '#171717' }}>
        <Container maxWidth="lg">
          <Grid container spacing={6} alignItems="center">
            <Grid item xs={12} md={6}>
              <Box
                component="img"
                src="/images/showroom.jpg"
                alt="Our Showroom"
                sx={{
                  width: '100%',
                  height: 'auto',
                  borderRadius: 2,
                  boxShadow: '0 4px 30px rgba(0, 0, 0, 0.1)',
                }}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <Typography variant="h3" sx={{ mb: 3, fontWeight: 700 }}>
                About Us
              </Typography>
              <Typography variant="body1" sx={{ mb: 3, color: 'rgba(255,255,255,0.8)' }}>
                Welcome to [Showroom Name], your trusted local car dealership serving the community since 2010. We specialize in quality used cars and affordable new vehicles, making car ownership accessible to everyone.
              </Typography>
              <Typography variant="body1" sx={{ mb: 4, color: 'rgba(255,255,255,0.8)' }}>
                Our team of experienced professionals is dedicated to helping you find the perfect car that fits both your needs and budget. We offer competitive finance options and outstanding after-sales service.
              </Typography>
            </Grid>
          </Grid>
        </Container>
      </Box>

      {/* Contact Us Section */}
      <Box sx={{ py: 8, backgroundColor: '#1a1a1a' }}>
        <Container maxWidth="lg">
          <Grid container spacing={6}>
            <Grid item xs={12} md={6}>
              <Typography variant="h3" sx={{ mb: 4, fontWeight: 700 }}>
                Contact Us
              </Typography>
              
              <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                <TextField
                  label="Name"
                  variant="outlined"
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(255,255,255,0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: 'rgba(255,255,255,0.5)',
                      },
                    },
                    '& .MuiInputLabel-root': {
                      color: 'rgba(255,255,255,0.7)',
                    },
                    '& .MuiInputBase-input': {
                      color: 'white',
                    },
                  }}
                />
                
                <TextField
                  label="Email"
                  type="email"
                  variant="outlined"
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(255,255,255,0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: 'rgba(255,255,255,0.5)',
                      },
                    },
                    '& .MuiInputLabel-root': {
                      color: 'rgba(255,255,255,0.7)',
                    },
                    '& .MuiInputBase-input': {
                      color: 'white',
                    },
                  }}
                />
                
                <TextField
                  label="Message"
                  multiline
                  rows={4}
                  variant="outlined"
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      '& fieldset': {
                        borderColor: 'rgba(255,255,255,0.3)',
                      },
                      '&:hover fieldset': {
                        borderColor: 'rgba(255,255,255,0.5)',
                      },
                    },
                    '& .MuiInputLabel-root': {
                      color: 'rgba(255,255,255,0.7)',
                    },
                    '& .MuiInputBase-input': {
                      color: 'white',
                    },
                  }}
                />
                
                <Button 
                  type="submit"
                  variant="contained"
                  size="large"
                  sx={{
                    backgroundColor: '#dc2626',
                    '&:hover': {
                      backgroundColor: '#b91c1c',
                    },
                  }}
                >
                  Send Message
                </Button>
              </Box>
